import React from 'react';
import { useState } from 'react';
import { usePlayerStore } from '../store/playerStore';
import { Player } from '../types/player';

const PlayerTable: React.FC = () => {
  const { 
    filteredPlayers, 
    stats, 
    filters, 
    sort, 
    setFilters, 
    setSort, 
    resetFilters 
  } = usePlayerStore();

  const [expandedRows, setExpandedRows] = useState<Set<string>>(new Set());

  // Helper function to safely format numbers
  const formatNumber = (value: number | undefined | null, decimals: number = 2) => {
    if (value === undefined || value === null) return '0.00';
    return value.toFixed(decimals);
  };

  const toggleRow = (playerName: string) => {
    console.log('Before toggle - Expanded rows:', Array.from(expandedRows));
    setExpandedRows(prev => {
      const newExpanded = new Set(prev);
      if (newExpanded.has(playerName)) {
        newExpanded.delete(playerName);
      } else {
        newExpanded.add(playerName);
      }
      console.log('After toggle - New expanded rows:', Array.from(newExpanded));
      return newExpanded;
    });
  };

  const handleSort = (field: keyof Player) => {
    console.log('Sorting by:', field, 'Current sort:', sort);
    const newDirection = sort.field === field && sort.direction === 'asc' ? 'desc' : 'asc';
    console.log('New sort direction:', newDirection);
    setSort({ field, direction: newDirection });
  };

  const handleFilterChange = (key: keyof typeof filters, value: string | number) => {
    console.log('Filter change - Key:', key, 'Value:', value, 'Current filters:', filters);
    setFilters({ ...filters, [key]: value });
  };

  const handleReset = () => {
    console.log('Resetting filters');
    resetFilters();
  };

  const getSortIcon = (field: keyof Player) => {
    if (sort.field !== field) return '↕️';
    return sort.direction === 'asc' ? '↑' : '↓';
  };

  if (!stats) {
    return null;
  }

  return (
    <div className="space-y-6">
      {/* Filters */}
      <div className="bg-card p-6 rounded-lg border">
        <h3 className="text-xl font-semibold text-foreground mb-4">Filters</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          <div>
            <label className="block text-sm font-medium text-foreground mb-2">
              Search
            </label>
            <input
              type="text"
              value={filters.search}
              onChange={(e) => handleFilterChange('search', e.target.value)}
              placeholder="Search players, teams, nations..."
              className="w-full px-3 py-2 border border-input rounded-md bg-background text-foreground"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-foreground mb-2">
              Position
            </label>
            <select
              value={filters.position}
              onChange={(e) => handleFilterChange('position', e.target.value)}
              className="w-full px-3 py-2 border border-input rounded-md bg-background text-foreground"
            >
              <option value="">All Positions</option>
              {stats.positions.map(pos => (
                <option key={pos} value={pos}>{pos}</option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-foreground mb-2">
              Team
            </label>
            <select
              value={filters.team}
              onChange={(e) => handleFilterChange('team', e.target.value)}
              className="w-full px-3 py-2 border border-input rounded-md bg-background text-foreground"
            >
              <option value="">All Teams</option>
              {stats.teams.map(team => (
                <option key={team} value={team}>{team}</option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-foreground mb-2">
              Nation
            </label>
            <select
              value={filters.nation}
              onChange={(e) => handleFilterChange('nation', e.target.value)}
              className="w-full px-3 py-2 border border-input rounded-md bg-background text-foreground"
            >
              <option value="">All Nations</option>
              {stats.nations.map(nation => (
                <option key={nation} value={nation}>{nation}</option>
              ))}
            </select>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-4">
          <div>
            <label className="block text-sm font-medium text-foreground mb-2">
              Min Goals
            </label>
            <input
              type="number"
              value={filters.minGoals}
              onChange={(e) => handleFilterChange('minGoals', parseInt(e.target.value) || 0)}
              min="0"
              className="w-full px-3 py-2 border border-input rounded-md bg-background text-foreground"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-foreground mb-2">
              Min Assists
            </label>
            <input
              type="number"
              value={filters.minAssists}
              onChange={(e) => handleFilterChange('minAssists', parseInt(e.target.value) || 0)}
              min="0"
              className="w-full px-3 py-2 border border-input rounded-md bg-background text-foreground"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-foreground mb-2">
              Min Minutes
            </label>
            <input
              type="number"
              value={filters.minMinutes}
              onChange={(e) => handleFilterChange('minMinutes', parseInt(e.target.value) || 0)}
              min="0"
              className="w-full px-3 py-2 border border-input rounded-md bg-background text-foreground"
            />
          </div>
        </div>

        <div className="mt-4">
          <button
            onClick={handleReset}
            className="px-4 py-2 bg-secondary text-secondary-foreground rounded-md hover:bg-secondary/80"
          >
            Reset Filters
          </button>
        </div>
      </div>

      {/* Results Summary */}
      <div className="bg-card p-4 rounded-lg border">
        <p className="text-foreground">
          Showing {filteredPlayers.length} of {stats.totalPlayers} players
        </p>
      </div>

      {/* Player Table */}
      <div className="bg-card rounded-lg border overflow-hidden">
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-muted">
              <tr>
                <th className="px-4 py-3 text-left text-sm font-medium text-foreground">
                  Player
                </th>
                <th 
                  className="px-4 py-3 text-left text-sm font-medium text-foreground cursor-pointer hover:bg-muted/50"
                  onClick={() => handleSort('Squad')}
                >
                  Team {getSortIcon('Squad')}
                </th>
                <th 
                  className="px-4 py-3 text-left text-sm font-medium text-foreground cursor-pointer hover:bg-muted/50"
                  onClick={() => handleSort('Pos')}
                >
                  Pos {getSortIcon('Pos')}
                </th>
                <th 
                  className="px-4 py-3 text-left text-sm font-medium text-foreground cursor-pointer hover:bg-muted/50"
                  onClick={() => handleSort('Gls')}
                >
                  Goals {getSortIcon('Gls')}
                </th>
                <th 
                  className="px-4 py-3 text-left text-sm font-medium text-foreground cursor-pointer hover:bg-muted/50"
                  onClick={() => handleSort('Ast')}
                >
                  Assists {getSortIcon('Ast')}
                </th>
                <th 
                  className="px-4 py-3 text-left text-sm font-medium text-foreground cursor-pointer hover:bg-muted/50"
                  onClick={() => handleSort('Min')}
                >
                  Minutes {getSortIcon('Min')}
                </th>
                <th className="px-4 py-3 text-left text-sm font-medium text-foreground">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="divide-y divide-border">
              {filteredPlayers.map((player) => (
                <React.Fragment key={player.Player}>
                  <tr className="hover:bg-muted/50">
                    <td className="px-4 py-3">
                      <div>
                        <p className="font-medium text-foreground">{player.Player}</p>
                        <p className="text-sm text-muted-foreground">{player.Nation}</p>
                      </div>
                    </td>
                    <td className="px-4 py-3 text-foreground">{player.Squad}</td>
                    <td className="px-4 py-3 text-foreground">{player.Pos}</td>
                    <td className="px-4 py-3 text-foreground">{player.Gls || 0}</td>
                    <td className="px-4 py-3 text-foreground">{player.Ast || 0}</td>
                    <td className="px-4 py-3 text-foreground">{player.Min || 0}</td>
                    <td className="px-4 py-3">
                      <button
                        onClick={() => toggleRow(player.Player)}
                        className="px-3 py-1 bg-primary text-primary-foreground rounded-md hover:bg-primary/90 text-sm"
                      >
                        {expandedRows.has(player.Player) ? 'Hide' : 'Details'}
                      </button>
                    </td>
                  </tr>
                  {expandedRows.has(player.Player) && (
                    <tr>
                      <td colSpan={7} className="px-4 py-4 bg-muted/30">
                        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                          <div>
                            <h4 className="font-medium text-foreground mb-2">Basic Info</h4>
                            <div className="space-y-1 text-sm">
                              <p><span className="text-muted-foreground">Age:</span> {2024 - (player.Born || 0)}</p>
                              <p><span className="text-muted-foreground">Matches:</span> {player.MP || 0}</p>
                              <p><span className="text-muted-foreground">Starts:</span> {player.Starts || 0}</p>
                              <p><span className="text-muted-foreground">90s:</span> {formatNumber(player['90s'] || 0, 1)}</p>
                            </div>
                          </div>

                          <div>
                            <h4 className="font-medium text-foreground mb-2">Goals & Assists</h4>
                            <div className="space-y-1 text-sm">
                              <p><span className="text-muted-foreground">Goals:</span> {player.Gls || 0}</p>
                              <p><span className="text-muted-foreground">Assists:</span> {player.Ast || 0}</p>
                              <p><span className="text-muted-foreground">G+A:</span> {player['G+A'] || 0}</p>
                              <p><span className="text-muted-foreground">G-PK:</span> {player['G-PK'] || 0}</p>
                            </div>
                          </div>

                          <div>
                            <h4 className="font-medium text-foreground mb-2">Per 90 Stats</h4>
                            <div className="space-y-1 text-sm">
                              <p><span className="text-muted-foreground">Goals/90:</span> {formatNumber(player['Gls.1'] || 0)}</p>
                              <p><span className="text-muted-foreground">Assists/90:</span> {formatNumber(player['Ast.1'] || 0)}</p>
                              <p><span className="text-muted-foreground">G+A/90:</span> {formatNumber(player['G+A.1'] || 0)}</p>
                              <p><span className="text-muted-foreground">xG/90:</span> {formatNumber(player['xG.1'] || 0)}</p>
                            </div>
                          </div>

                          <div>
                            <h4 className="font-medium text-foreground mb-2">Other Stats</h4>
                            <div className="space-y-1 text-sm">
                              <p><span className="text-muted-foreground">Yellow Cards:</span> {player.CrdY || 0}</p>
                              <p><span className="text-muted-foreground">Red Cards:</span> {player.CrdR || 0}</p>
                              <p><span className="text-muted-foreground">Progressive Carries:</span> {player.PrgC || 0}</p>
                              <p><span className="text-muted-foreground">Progressive Passes:</span> {player.PrgP || 0}</p>
                            </div>
                          </div>
                        </div>
                      </td>
                    </tr>
                  )}
                </React.Fragment>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default PlayerTable;
