import React from 'react';

interface NavigationProps {
  currentView: 'dashboard' | 'charts' | 'table';
  onViewChange: (view: 'dashboard' | 'charts' | 'table') => void;
}

const Navigation: React.FC<NavigationProps> = ({ currentView, onViewChange }) => {
  return (
    <nav className="bg-primary/95 backdrop-blur-sm sticky top-0 z-50 border-b border-accent/10 shadow-lg">
      <div className="container mx-auto px-4">
        <div className="flex items-center justify-center md:justify-start space-x-1">
          <button
            onClick={() => onViewChange('dashboard')}
            className={`px-4 py-3 text-sm font-medium transition-all duration-200 relative ${
              currentView === 'dashboard'
                ? 'text-white'
                : 'text-primary-foreground/70 hover:text-white'
            }`}
          >
            Dashboard
            {currentView === 'dashboard' && (
              <div className="absolute bottom-0 left-0 w-full h-0.5 bg-accent animate-pulse-subtle"></div>
            )}
          </button>

          <button
            onClick={() => onViewChange('charts')}
            className={`px-4 py-3 text-sm font-medium transition-all duration-200 relative ${
              currentView === 'charts'
                ? 'text-white'
                : 'text-primary-foreground/70 hover:text-white'
            }`}
          >
            Charts
            {currentView === 'charts' && (
              <div className="absolute bottom-0 left-0 w-full h-0.5 bg-accent animate-pulse-subtle"></div>
            )}
          </button>

          <button
            onClick={() => onViewChange('table')}
            className={`px-4 py-3 text-sm font-medium transition-all duration-200 relative ${
              currentView === 'table'
                ? 'text-white'
                : 'text-primary-foreground/70 hover:text-white'
            }`}
          >
            Players
            {currentView === 'table' && (
              <div className="absolute bottom-0 left-0 w-full h-0.5 bg-accent animate-pulse-subtle"></div>
            )}
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navigation;
