import React from 'react';
import { useState, useEffect } from 'react';
import { usePlayerStore } from './store/playerStore';
import { loadCSVData } from './services/csvService';
import Header from './components/Header';
import Navigation from './components/Navigation';
import Dashboard from './components/Dashboard';
import PlayerTable from './components/PlayerTable';
import Charts from './components/Charts';
import './index.css';

function App() {
  const { setPlayers, setLoading, setError, isLoading, error } = usePlayerStore();
  const [currentView, setCurrentView] = useState<'dashboard' | 'charts' | 'table'>('dashboard');

  useEffect(() => {
    const loadData = async () => {
      setLoading(true);
      setError(null);

      try {
        const players = await loadCSVData();
        setPlayers(players);
      } catch (err) {
        setError(err instanceof Error ? err.message : 'Failed to load data');
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, [setPlayers, setLoading, setError]);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary mx-auto"></div>
          <p className="mt-4 text-lg">Loading UCL statistics...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-destructive mb-4">Error Loading Data</h1>
          <p className="text-muted-foreground">{error}</p>
          <button 
            onClick={() => window.location.reload()} 
            className="mt-4 px-4 py-2 bg-primary text-primary-foreground rounded-md hover:bg-primary/90"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      <Header />
      <Navigation currentView={currentView} onViewChange={setCurrentView} />
      <main className="container mx-auto px-4 py-8">
        {currentView === 'dashboard' && <Dashboard />}
        {currentView === 'charts' && <Charts />}
        {currentView === 'table' && <PlayerTable />}
      </main>
    </div>
  );
}

export default App;
