import React from 'react';
import { usePlayerStore } from '../store/playerStore';

const Charts: React.FC = () => {
  const { stats } = usePlayerStore();

  if (!stats) {
    return null;
  }

  // Helper function to safely format numbers
  const formatNumber = (value: number | undefined | null, decimals: number = 2) => {
    if (value === undefined || value === null) return '0.00';
    return value.toFixed(decimals);
  };

  // Helper function to calculate safe percentage
  const calculatePercentage = (value: number, maxValue: number) => {
    if (!value || !maxValue) return 0;
    return (value / maxValue) * 100;
  };

  // Prepare data for charts
  const topScorersData = stats.topScorers.slice(0, 10).map((player, index) => ({
    name: player.Player,
    goals: player.Gls || 0,
    goalsPer90: player['Gls.1'] || 0,
    team: player.Squad,
    rank: index + 1
  }));

  const topAssistersData = stats.topAssisters.slice(0, 10).map((player, index) => ({
    name: player.Player,
    assists: player.Ast || 0,
    assistsPer90: player['Ast.1'] || 0,
    team: player.Squad,
    rank: index + 1
  }));

  const maxGoals = Math.max(...topScorersData.map(p => p.goals));
  const maxAssists = Math.max(...topAssistersData.map(p => p.assists));

  return (
    <div className="space-y-8">
      <h2 className="text-2xl font-bold text-foreground mb-6">Charts & Analytics</h2>

      {/* Top Scorers Chart */}
      <div className="bg-card p-6 rounded-lg border">
        <h3 className="text-xl font-semibold text-foreground mb-4">Top 10 Scorers</h3>
        <div className="space-y-4">
          {topScorersData.map((player) => (
            <div key={player.name} className="flex items-center space-x-4">
              <div className="w-8 h-8 bg-primary text-primary-foreground rounded-full flex items-center justify-center text-sm font-bold">
                {player.rank}
              </div>
              <div className="flex-1">
                <div className="flex items-center justify-between mb-1">
                  <span className="font-medium text-foreground">{player.name}</span>
                  <span className="text-sm text-muted-foreground">{player.team}</span>
                </div>
                <div className="w-full bg-muted rounded-full h-2">
                  <div 
                    className="bg-primary h-2 rounded-full transition-all duration-300"
                    style={{ width: `${calculatePercentage(player.goals, maxGoals)}%` }}
                  ></div>
                </div>
                <div className="flex justify-between text-xs text-muted-foreground mt-1">
                  <span>{player.goals} goals</span>
                  <span>{formatNumber(player.goalsPer90)} per 90</span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Top Assisters Chart */}
      <div className="bg-card p-6 rounded-lg border">
        <h3 className="text-xl font-semibold text-foreground mb-4">Top 10 Assisters</h3>
        <div className="space-y-4">
          {topAssistersData.map((player) => (
            <div key={player.name} className="flex items-center space-x-4">
              <div className="w-8 h-8 bg-primary text-primary-foreground rounded-full flex items-center justify-center text-sm font-bold">
                {player.rank}
              </div>
              <div className="flex-1">
                <div className="flex items-center justify-between mb-1">
                  <span className="font-medium text-foreground">{player.name}</span>
                  <span className="text-sm text-muted-foreground">{player.team}</span>
                </div>
                <div className="w-full bg-muted rounded-full h-2">
                  <div 
                    className="bg-primary h-2 rounded-full transition-all duration-300"
                    style={{ width: `${calculatePercentage(player.assists, maxAssists)}%` }}
                  ></div>
                </div>
                <div className="flex justify-between text-xs text-muted-foreground mt-1">
                  <span>{player.assists} assists</span>
                  <span>{formatNumber(player.assistsPer90)} per 90</span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Team Statistics */}
      <div className="bg-card p-6 rounded-lg border">
        <h3 className="text-xl font-semibold text-foreground mb-4">Team Distribution</h3>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          <div className="text-center p-4 bg-muted rounded-lg">
            <div className="text-2xl font-bold text-foreground">{stats.teams.length}</div>
            <div className="text-sm text-muted-foreground">Teams</div>
          </div>
          <div className="text-center p-4 bg-muted rounded-lg">
            <div className="text-2xl font-bold text-foreground">{stats.positions.length}</div>
            <div className="text-sm text-muted-foreground">Positions</div>
          </div>
          <div className="text-center p-4 bg-muted rounded-lg">
            <div className="text-2xl font-bold text-foreground">{stats.nations.length}</div>
            <div className="text-sm text-muted-foreground">Nations</div>
          </div>
          <div className="text-center p-4 bg-muted rounded-lg">
            <div className="text-2xl font-bold text-foreground">{stats.totalPlayers}</div>
            <div className="text-sm text-muted-foreground">Players</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Charts;
