import Papa from 'papaparse';
import { Player } from '../types/player';

export const loadCSVData = async (): Promise<Player[]> => {
  try {
    console.log('Attempting to fetch CSV data...');
    const response = await fetch('/UCL_24_25_Key_Data - Sheet1.csv');
    if (!response.ok) {
      throw new Error(`Failed to fetch CSV data: ${response.status} ${response.statusText}`);
    }
    const csvText = await response.text();
    console.log('CSV data fetched successfully, parsing...');

    return new Promise((resolve, reject) => {
      Papa.parse(csvText, {
        header: true,
        skipEmptyLines: true,
        transform: (value: string, field: string) => {
          // Convert numeric fields
          const numericFields = [
            'Born', 'MP', 'Starts', 'Min', '90s', 'Gls', 'Ast', 'G+A', 'G-PK', 
            'PK', 'PKatt', 'CrdY', 'CrdR', 'xG', 'npxG', 'xAG', 'npxG+xAG',
            'PrgC', 'PrgP', 'PrgR', 'Gls.1', 'Ast.1', 'G+A.1', 'G-PK.1',
            'G+A-PK', 'xG.1', 'xAG.1', 'xG+xAG', 'npxG.1', 'npxG+xAG.1'
          ] as string[];

          if (numericFields.includes(field)) {
            // Remove commas and quotes before parsing
            const cleanValue = value.replace(/[",]/g, '');
            const num = parseFloat(cleanValue);
            if (field === 'Min' || field === 'MP') {
              console.log(`Converting ${field} value: "${value}" -> "${cleanValue}" to number: ${num}`);
            }
            return isNaN(num) ? 0 : num;
          }

          return value;
        },
        complete: (results) => {
          if (results.errors.length > 0) {
            console.error('CSV parsing errors:', results.errors);
            reject(new Error('Failed to parse CSV data'));
            return;
          }

          const players = results.data as Player[];
          if (!players.length) {
            console.error('No player data found in CSV');
            reject(new Error('No player data found in CSV'));
            return;
          }
          console.log(`Successfully loaded ${players.length} players`);
          // Log a sample player to check data structure
          if (players.find(p => p.Player === 'Raphinha')) {
            console.log('Raphinha data:', players.find(p => p.Player === 'Raphinha'));
          }
          resolve(players);
        },
        error: (error: Error) => {
          console.error('Error parsing CSV:', error);
          reject(error);
        }
      });
    });
  } catch (error) {
    console.error('Error loading CSV data:', error);
    throw error instanceof Error ? error : new Error('Failed to load CSV data');
  }
};
